    return(

        <Container>
            <div>
                <header>
                    <p>Entradas</p>
                    <img src={incomeImg}alt="Entradas"/>
                </header>
                <strong>
                    {new Intl.NumberFormat('pt-BR',{
                        style: 'currency',
                        currency: 'BRL'
                    }).format(totalDeposits)}
                    </strong>
            </div>
            <div>
                <header>
                    <p>Saida</p>
                    <img src={outcomeImg}alt="Saidas"/>
                </header>
                <strong> - R$500,00</strong>
            </div>
            <div className="highlight-background">
                <header>
                    <p>Total</p>
                    <img src={totalImg}alt="Total"/>
                </header>
                <strong>R$500,00</strong>
            </div>
        </Container>
    );
}
